import React, { useContext, useEffect, useState } from 'react';
import context from'../context/context';
import './movies_posters.css';
import Header from './Header';

function Lista(){

    const [movies, setMovies] = useState([]);
    const [searchValue, setSearchValue ] = useState('');
    const img_path = 'https://image.tmdb.org/t/p/w500/'
    const url = 'https://api.themoviedb.org/3';

    const fetchMovies = async (searchValue) => {
        const type = searchValue ? `search/movie?api_key=3e9594956f7bdfe6a28130cd66f6d581&language=en-US&query=${searchValue}=a&page=1&include_adult=false` : 'discover/movie?api_key=3e9594956f7bdfe6a28130cd66f6d581'
        const data = await fetch(`${url}/${type}`)
        .then((response) => (response.json()))
        .catch((err) => console.log(err));
        setMovies(data.results);
    }

    const searchMovies = (e) => {
        e.preventDefault();
       fetchMovies(searchValue);
    }

    console.log(movies);
    useEffect(() => {
        fetchMovies();
    },[])

    return (
        <>
        <Header/>
        <form onSubmit={searchMovies}>
            <input 
                placeholder="Find a film" 
                onChange={(e) => setSearchValue(e.target.value)}    
            />
            <button>Search</button>
        </form>
        <section className='container'>
            {movies.map((filmes) =>
            <div>
                <section className='moviePoster'>
                    <img src={`${img_path}${filmes.poster_path}`} alt={`${filmes.original_title} poster`} />
                    <h3>{filmes.original_title}</h3>
                </section>
                <p>{filmes.page}</p>
            </div>
                )}
        </section>
        </>
    )
}

export default Lista;
