import React, { useContext, useEffect, useState } from 'react';
import context from'../context/context';
import './movies_posters.css';

function Lista(){

    const [movies, setMovies] = useState([]);
    const img_path = 'https://image.tmdb.org/t/p/w500'
    const url = 'https://api.themoviedb.org/3'; 
    const fetchMovies = async () => {
        const data = await fetch(`${url}/discover/movie?api_key=3e9594956f7bdfe6a28130cd66f6d581`)
        .then((response) => (response.json()))
        .catch((err) => console.log(err));
        setMovies(data.results);
    }
    console.log(movies);
    useEffect(() => {
        fetchMovies();
    },[])

    return (
        <section className='container'>
            {movies.map((filmes) => 
                <section className='moviePoster'>
                    <img src={`${img_path}${filmes.poster_path}`} alt={`${filmes.original_title} poster`} />
                    <h3>{filmes.original_title}</h3>
                </section>)}
        </section>
    )
}

export default Lista;
