import React, { useContext, useEffect, useState } from 'react';
import context from'../context/context';
import './movies_posters.css';
import Header from './Header';

function Lista(){

    const [movies, setMovies] = useState([]);
    const [trailer, setTrailer ] = useState([]);
    const [selectedMovie, setSelectedMovie] = useState({});
    const [searchValue, setSearchValue ] = useState('');
    const img_path = 'https://image.tmdb.org/t/p/'
    const url = 'https://api.themoviedb.org/3';
    const movieUrl = 'https://api.themoviedb.org/3/movie/338953/videos?api_key=3e9594956f7bdfe6a28130cd66f6d581&language=en-US';

    const fetchMovies = async (searchValue) => {
        const type = searchValue ? `search/multi?api_key=3e9594956f7bdfe6a28130cd66f6d581&language=en-Us&query=${searchValue}&page=1&include_adult=false` : 'discover/movie?api_key=3e9594956f7bdfe6a28130cd66f6d581'
        const data = await fetch(`${url}/${type}`)
        .then((response) => (response.json()))
        .catch((err) => console.log(err));
        setMovies(data.results);
        setSelectedMovie(data.results[0]);
        console.log(selectedMovie);
    }

    const fetchVideo = async() =>{
        const moviestrailers = await fetch(movieUrl)
        .then((res) => res.json())
        .catch((err)  => console.log(err));
    }

    const searchMovies = (e) => {
        e.preventDefault();
       fetchMovies(searchValue);
    }


    useEffect(() => {
        fetchMovies();
        fetchVideo();
    },[])

    return (
        <>
        <Header/>
        <div className='hero-content center' style={{backgroundImage: `${img_path}original${selectedMovie.backdrop_path}`}} >
        <button id='hero-button' >Play trailer</button>
            <h2 id='hero-title' >{selectedMovie.title}</h2>
            <p>{selectedMovie.overview}</p>
        </div>
        <form onSubmit={searchMovies}>
            <input 
                placeholder="Find a film" 
                onChange={(e) => setSearchValue(e.target.value)}    
            />
            <button>Search</button>
        </form>
        <section className='container'>
            {movies.map((filmes) =>  filmes.poster_path ?
            <div>
                <section className='moviePoster'>
                    <img src={`${img_path}/w500/${filmes.poster_path}`} alt={`${filmes.original_title} poster`} />
                    <h3>{filmes.original_title}</h3>
                </section>
            </div>
                :<div> 
                        No poster found 
                    <h3> {filmes.original_title} </h3>
                </div>

                )}
        </section>
        </>
    )
}

export default Lista;
